package Demo;

public class byteDatatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a = 127;
		byte b = -127;
		short s = 32676;
		long l1 = 100000000;
		long l2 = 563785439;
		a++;
		System.out.println(a);
		System.out.println(a+b);
		System.out.println(s);
		System.out.println(l1*l2);
	}

}
